create table players
(id int not null auto_increment,
player char(50),
position char(20),
age int,
height double,
weight double,
year double(4,2),
primary key (id)
);


alter table players
add column team char(20);

update players
set team = 
(select team
from teams
where players.id = teams.id); -- inserted team in players from teams table
